/**
 * Payment App Main Component
 * Evidence: artifacts/reports/apps_web_src_App.jsx.json
 */
import React, { useState, useEffect } from 'react';
import { CheckoutForm } from './components/CheckoutForm';

export function App() {
  const [paymentMode, setPaymentMode] = useState(
    process.env.REACT_APP_PAYMENT_MODE || 'mock'
  );
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    // Load transactions from localStorage
    const stored = localStorage.getItem('payment_transactions');
    if (stored) {
      setTransactions(JSON.parse(stored));
    }
  }, []);

  const handlePaymentComplete = (transaction) => {
    const updated = [...transactions, transaction];
    setTransactions(updated);
    localStorage.setItem('payment_transactions', JSON.stringify(updated));
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>Payment Processing System</h1>
        <div className="mode-indicator">
          Mode: <span className={`mode-${paymentMode}`}>{paymentMode.toUpperCase()}</span>
        </div>
      </header>

      <main className="app-main">
        <section className="checkout-section">
          <h2>Checkout</h2>
          <CheckoutForm 
            mode={paymentMode}
            onComplete={handlePaymentComplete}
          />
        </section>

        <section className="history-section">
          <h2>Transaction History</h2>
          <div className="transactions">
            {transactions.map((tx, idx) => (
              <div key={idx} className="transaction-item">
                <span className="tx-id">{tx.id}</span>
                <span className="tx-amount">${tx.amount}</span>
                <span className="tx-status">{tx.status}</span>
                <span className="tx-time">{new Date(tx.timestamp).toLocaleString()}</span>
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer className="app-footer">
        <p>Evidence: artifacts/reports/apps_web_src_App.jsx.json</p>
        <p>Determinism: seed=1337, clock=2025-01-01T00:00:00Z</p>
      </footer>
    </div>
  );
}

export default App;

/**
 * Checkout Form Component with Idempotency
 * Evidence: artifacts/reports/apps_web_src_components_CheckoutForm.jsx.json
 */
import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

export function CheckoutForm({ mode, onComplete }) {
  const [amount, setAmount] = useState('');
  const [email, setEmail] = useState('');
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setProcessing(true);
    setError(null);

    const idempotencyKey = uuidv4();

    try {
      const response = await fetch('/api/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-Idempotency-Key': idempotencyKey,
          'X-Payment-Mode': mode
        },
        body: JSON.stringify({
          amount: parseFloat(amount),
          email,
          timestamp: new Date().toISOString()
        })
      });

      if (!response.ok) {
        throw new Error(`Payment failed: ${response.statusText}`);
      }

      const result = await response.json();

      onComplete({
        id: result.paymentIntentId,
        amount: parseFloat(amount),
        email,
        status: result.status,
        idempotencyKey,
        timestamp: new Date().toISOString()
      });

      // Reset form
      setAmount('');
      setEmail('');
    } catch (err) {
      setError(err.message);
    } finally {
      setProcessing(false);
    }
  };

  return (
    <form className="checkout-form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={processing}
        />
      </div>

      <div className="form-group">
        <label htmlFor="amount">Amount ($)</label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          min="0.01"
          step="0.01"
          required
          disabled={processing}
        />
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <button type="submit" disabled={processing}>
        {processing ? 'Processing...' : `Pay $${amount || '0.00'}`}
      </button>

      <div className="form-footer">
        <small>Idempotency ensures no duplicate charges</small>
      </div>
    </form>
  );
}

/**
 * Checkout API Route with Idempotency
 * Evidence: artifacts/reports/apps_api_src_routes_checkout.mjs.json
 */
import crypto from 'crypto';
import { v4 as uuidv4 } from 'uuid';

const idempotencyCache = new Map();

export function setupCheckoutRoute(app) {
  app.post('/api/checkout', async (req, res) => {
    const idempotencyKey = req.headers['x-idempotency-key'];
    const paymentMode = req.headers['x-payment-mode'] || process.env.PAYMENT_MODE || 'mock';

    // Check idempotency cache
    if (idempotencyKey && idempotencyCache.has(idempotencyKey)) {
      console.log(`[IDEMPOTENCY] Returning cached response for key: ${idempotencyKey}`);
      return res.json(idempotencyCache.get(idempotencyKey));
    }

    const { amount, email } = req.body;

    // Validate input
    if (!amount || amount <= 0) {
      return res.status(400).json({ error: 'Invalid amount' });
    }

    if (!email || !email.includes('@')) {
      return res.status(400).json({ error: 'Invalid email' });
    }

    // Generate payment intent
    const paymentIntentId = `pi_${uuidv4()}`;
    const timestamp = new Date().toISOString();

    let response;

    if (paymentMode === 'live') {
      // Live mode - would integrate with real payment provider
      // For demo, we simulate a successful payment
      response = {
        paymentIntentId,
        amount,
        email,
        status: 'succeeded',
        mode: 'live',
        timestamp,
        signature: crypto
          .createHmac('sha256', process.env.PAYMENT_SECRET || 'secret')
          .update(`${paymentIntentId}:${amount}:${email}`)
          .digest('hex')
      };
    } else {
      // Mock mode
      response = {
        paymentIntentId,
        amount,
        email,
        status: 'mock_succeeded',
        mode: 'mock',
        timestamp,
        mockSignature: crypto
          .createHash('sha256')
          .update(`${paymentIntentId}:${amount}:${email}`)
          .digest('hex')
      };
    }

    // Store in idempotency cache
    if (idempotencyKey) {
      idempotencyCache.set(idempotencyKey, response);

      // Clean old entries after 24 hours
      setTimeout(() => {
        idempotencyCache.delete(idempotencyKey);
      }, 24 * 60 * 60 * 1000);
    }

    // Log the transaction
    console.log(`[CHECKOUT] Payment processed:`, {
      id: paymentIntentId,
      amount,
      email,
      mode: paymentMode,
      idempotencyKey
    });

    res.json(response);
  });
}

export default setupCheckoutRoute;

/**
 * Webhook Handler with HMAC Verification
 * Evidence: artifacts/reports/apps_api_src_routes_webhook.mjs.json
 */
import crypto from 'crypto';

const processedWebhooks = new Set();

export function setupWebhookRoute(app) {
  app.post('/api/webhook', async (req, res) => {
    const signature = req.headers['x-webhook-signature'];
    const webhookId = req.headers['x-webhook-id'];
    const timestamp = req.headers['x-webhook-timestamp'];

    // Check if already processed (idempotency)
    if (webhookId && processedWebhooks.has(webhookId)) {
      console.log(`[WEBHOOK] Already processed: ${webhookId}`);
      return res.json({ status: 'already_processed' });
    }

    // Verify timestamp (prevent replay attacks)
    const now = Date.now();
    const webhookTime = new Date(timestamp).getTime();
    const timeDiff = Math.abs(now - webhookTime);

    if (timeDiff > 5 * 60 * 1000) { // 5 minutes tolerance
      console.error(`[WEBHOOK] Timestamp too old: ${timestamp}`);
      return res.status(400).json({ error: 'Webhook timestamp expired' });
    }

    // Construct payload for signature verification
    const payload = `${webhookId}.${timestamp}.${JSON.stringify(req.body)}`;

    // Verify HMAC signature
    const webhookSecret = process.env.WEBHOOK_SECRET || 'webhook_secret_key';
    const expectedSignature = crypto
      .createHmac('sha256', webhookSecret)
      .update(payload)
      .digest('hex');

    if (signature !== expectedSignature) {
      console.error(`[WEBHOOK] Invalid signature. Expected: ${expectedSignature}, Got: ${signature}`);
      return res.status(401).json({ error: 'Invalid webhook signature' });
    }

    // Process the webhook
    const { event, data } = req.body;

    console.log(`[WEBHOOK] Processing event: ${event}`, {
      webhookId,
      timestamp,
      data
    });

    // Handle different event types
    let result;
    switch (event) {
      case 'payment.succeeded':
        result = handlePaymentSucceeded(data);
        break;
      case 'payment.failed':
        result = handlePaymentFailed(data);
        break;
      case 'refund.created':
        result = handleRefundCreated(data);
        break;
      default:
        console.log(`[WEBHOOK] Unknown event type: ${event}`);
        result = { status: 'unknown_event' };
    }

    // Mark as processed
    if (webhookId) {
      processedWebhooks.add(webhookId);

      // Clean up after 24 hours
      setTimeout(() => {
        processedWebhooks.delete(webhookId);
      }, 24 * 60 * 60 * 1000);
    }

    res.json({
      status: 'processed',
      webhookId,
      result
    });
  });
}

function handlePaymentSucceeded(data) {
  console.log('[WEBHOOK] Payment succeeded:', data);
  return { action: 'payment_recorded' };
}

function handlePaymentFailed(data) {
  console.log('[WEBHOOK] Payment failed:', data);
  return { action: 'payment_failure_recorded' };
}

function handleRefundCreated(data) {
  console.log('[WEBHOOK] Refund created:', data);
  return { action: 'refund_recorded' };
}

export default setupWebhookRoute;

/**
 * Payment API Server Main Entry
 * Evidence: artifacts/reports/apps_api_src_index.mjs.json
 */
import express from 'express';
import cors from 'cors';
import { setupCheckoutRoute } from './routes/checkout.mjs';
import { setupWebhookRoute } from './routes/webhook.mjs';

const app = express();
const PORT = process.env.PORT || 3001;

// Middleware
app.use(cors());
app.use(express.json());

// Request logging
app.use((req, res, next) => {
  console.log(`[${new Date().toISOString()}] ${req.method} ${req.path}`);
  next();
});

// Health check
app.get('/health', (req, res) => {
  res.json({
    status: 'healthy',
    timestamp: new Date().toISOString(),
    mode: process.env.PAYMENT_MODE || 'mock',
    determinism: {
      seed: process.env.DETERMINISM_SEED || '1337',
      clock: process.env.DETERMINISM_CLOCK || '2025-01-01T00:00:00Z'
    }
  });
});

// Setup routes
setupCheckoutRoute(app);
setupWebhookRoute(app);

// Error handling
app.use((err, req, res, next) => {
  console.error('[ERROR]', err);
  res.status(500).json({
    error: 'Internal server error',
    message: err.message
  });
});

// 404 handler
app.use((req, res) => {
  res.status(404).json({
    error: 'Not found',
    path: req.path
  });
});

// Start server
if (import.meta.url === `file://${process.argv[1]}`) {
  app.listen(PORT, () => {
    console.log(`[SERVER] Payment API running on port ${PORT}`);
    console.log(`[SERVER] Mode: ${process.env.PAYMENT_MODE || 'mock'}`);
    console.log(`[SERVER] Evidence traces in artifacts/reports/`);
  });
}

export default app;

/**
 * Payment System Theme Styles
